import { z } from "zod";
export const WorkflowSchema = z.enum([
    "streamer",
    "writers_room",
    "brainstorm",
    "debate",
    "podcast",
]);
export const CaptureModeSchema = z.enum(["audio", "video", "av"]);
export const ParticipantSchema = z.object({
    id: z.string(),
    name: z.string(),
});
export const SessionConfigSchema = z.object({
    sessionId: z.string().optional(), // Generated by server if not provided
    workflow: WorkflowSchema,
    captureMode: CaptureModeSchema,
    title: z.string().optional(),
    participants: z.array(ParticipantSchema).default([]),
    startedAt: z.number().optional(), // Generated by server if not provided
});
